"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[771],{8771:function(t,e){e.Z=[{shortAuthorName:"\u4f5c\u8005/\u5085\u55bb",author:"\u5085\u55bb",coverUrl:"/img/history/the-big-bang-1.jpg",slug:"the-big-bang",title:"The Big Bang",recommended:!0,contents:[{type:"paragraph",text:"1971 \u5e74\uff0c\u6e05\u83ef\u6821\u9577\u5f90\u8ce2\u4fee\u9080\u8acb\u5728\u7f8e\u570b NASA \u4efb\u8077\u7684\u4e18\u5b8f\u7fa9\u6559\u6388\u5230\u6e05\u83ef\u5ba2\u5ea7\u8a2a\u554f\u4e00\u5b78\u671f\uff0c\u958b\u8a2d\u300c\u5929\u6587\u7269\u7406\u300d\u8ab2\u7a0b\uff0c\u540c\u6642\u4e5f\u5317\u4e0a\u5230\u81fa\u5927\u6388\u8ab2\u3002"},{type:"paragraph",text:"\u4e18\u5b8f\u7fa9\u611f\u89ba\u5230\u81ea\u5df1\u505c\u7559\u6642\u9593\u77ed\u66ab\uff0c\u50c5\u50c5\u900f\u904e\u8ab2\u5802\u6559\u5b78\u4e0d\u80fd\u5920\u5145\u5206\u767c\u63ee\u6b64\u884c\u56de\u81fa\u7684\u4f5c\u7528\uff0c\u800c\u958b\u59cb\u898f\u5283\u8ab2\u5916\u5e36\u9818\u5b78\u751f\u88fd\u4f5c\u5929\u6587\u671b\u9060\u93e1\u3001\u5b78\u7fd2\u89c0\u6e2c\u6280\u8853\uff0c\u6216\u8a31\u80fd\u5920\u5f15\u5c0e\u5b78\u751f\u9ad4\u6703\u63a5\u89f8\u5929\u6587\u7684\u610f\u8da3\uff0c\u7559\u4e0b\u66f4\u9577\u9060\u7684\u5f71\u97ff\u3002"},{type:"paragraph",text:"\u671b\u9060\u93e1\u88fd\u4f5c\u8a08\u756b\u63d0\u51fa\u4e4b\u5f8c\uff0c\u6f38\u6f38\u5f97\u5230\u5b78\u751f\u97ff\u61c9\uff0c\u7531\u9ad8\u5e74\u7d1a\u7684\u7684\u5f90\u9b41\u68ee\u3001\u8521\u6c11\u96c4\u3001\u9ec3\u9298\u7235\u548c\u535a\u58eb\u73ed\u7684\u9673\u6587\u5178\u8ca0\u8cac\u88fd\u4f5c\u93e1\u67b6\u3001\u78e8\u93e1\u6a5f\u3001\u548c\u96fb\u5b50\u63a7\u5236\u7cfb\u7d71\uff1b\u5927\u4e00\u7684\u7684\u9673\u6631\u5be7\u3001\u9127\u5ef6\u748b\u3001\u6c88\u91c7\u860b\u3001\u548c\u8449\u8fce\u6625\u8ca0\u8cac\u5c07\u93e1\u677f\u7814\u78e8\u6210\u4e3b\u93e1\u3002"},{type:"paragraph",text:"\u6e05\u83ef\u7684\u5176\u4ed6\u8001\u5e2b\u4e5f\u76f8\u7576\u95dc\u6ce8\u9019\u500b\u8a08\u756b\uff0c\u674e\u6021\u56b4\u3001\u738b\u4f01\u7965\u3001\u6c88\u541b\u5c71\u3001\u6bdb\u9ad8\u6587\u7b49\u9f0e\u529b\u76f8\u52a9\uff0c\u5354\u52a9\u7c4c\u63aa\u5efa\u9020\u5929\u6587\u53f0\u7684\u7d93\u8cbb\uff0c\u5a92\u5408\u570b\u79d1\u6703\u79d1\u5100\u4e2d\u5fc3\u88fd\u4f5c\u96f6\u4ef6\u3002"},{type:"unorderList",styleType:"disc",title:"\u8a08\u756b\u57f7\u884c\u6642\u7a0b\uff1a",list:["\u56db\u6708\u4e2d\uff1a\u88fd\u4f5c\u516b\u540b\u725b\u9813\u5f0f\u671b\u9060\u93e1\u3002","\u4e03\u6708\u4e2d\uff1a\u516b\u540b\u725b\u9813\u5f0f\u671b\u9060\u93e1\u5b8c\u6210\u3002\u671b\u9060\u93e1\u7684\u8d64\u9053\u5100\u7531\u5f90\u9b41\u68ee\u8a2d\u8a08\uff0c\u59d4\u8a17\u570b\u79d1\u6703\u7cbe\u5bc6\u79d1\u5b78\u5100\u5668\u4e2d\u5fc3\u88fd\u4f5c\u3002","\u516b\u6708\u521d\uff1a\u7c4c\u5099\u88fd\u4f5c\u5341\u516d\u540b\u671b\u9060\u93e1\u3002","\u516b\u6708\u4e2d\uff1a\u9ad8\u5e74\u7d1a\u540c\u5b78\u5011\u88fd\u4f5c\u4e00\u81fa\u78e8\u93e1\u6a5f\uff0c\u904b\u7528\u78e8\u93e1\u6a5f\u53c8\u5feb\u901f\u88fd\u4f5c\u4e86\u4e00\u500b\u725b\u9813\u5f0f\u671b\u9060\u93e1\uff0c\u6210\u70ba\u5f8c\u4f86\u5341\u516d\u540b\u671b\u9060\u93e1\u7684\u5c0b\u661f\u93e1\u3002","\u516b\u6708\u5e95\uff1a\u5728\u78e8\u93e1\u6a5f\u7684\u8f14\u52a9\u4e4b\u4e0b\uff0c\u540c\u5b78\u5011\u9032\u4e00\u6b65\u88fd\u4f5c\u5341\u516d\u540b\u5361\u5f0f\u671b\u9060\u93e1\u3002","\u5341\u4e00\u6708\uff1a\u5341\u516d\u540b\u671b\u9060\u93e1\u5b8c\u6210\u3002"]},{type:"image",url:"/img/history/the-big-bang-3.jpg",description:"\u5341\u516d\u540b\u671b\u9060\u93e1\u93e1\u67b6\u88fd\u4f5c\u904e\u7a0b"},{type:"image",url:"/img/history/the-big-bang-2.jpg",description:"\u78e8\u93e1\u7d44\u5169\u4f4d\u540c\u5b78\u8207\u516b\u540b\u671b\u9060\u93e1\u5408\u5f71"},{type:"paragraph",text:"\u5341\u516d\u540b\u671b\u9060\u93e1\u5b8c\u6210\u5f8c\uff0c\u6210\u70ba\u7576\u6642\u5168\u570b\u93e1\u9762\u6700\u5927\u7684\u671b\u9060\u93e1\uff0c\u70ba\u9019\u500b\u65b0\u793e\u5718\u5e36\u4f86\u4e86\u5341\u8db3\u7684\u5ba3\u50b3\u6548\u679c\uff0c\u5730\u65b9\u5831\u7d19\u51fa\u73fe\u591a\u7bc7\u5831\u5c0e\u3002\u8a31\u591a\u793e\u53cb\u66fe\u8868\u793a\uff0c\u4ed6\u5011\u5728\u9ad8\u4e2d\u6642\u4ee3\u5c31\u5df2\u7d93\u77e5\u9053\u6e05\u5927\u5929\u6587\u53f0\u548c\u78e8\u93e1\u7684\u4e8b\u8e5f\uff0c\u5fc3\u56ae\u5f80\u4e4b\uff0c\u6210\u70ba\u8003\u4e0a\u6e05\u5927\u5f8c\u52a0\u5165\u5929\u6587\u793e\u7684\u9060\u56e0\u3002"},{type:"paragraph",text:"\u7136\u800c\u6642\u65e5\u6f38\u4e45\uff0c\u4fdd\u990a\u554f\u984c\u6d6e\u73fe\uff0c\u93e1\u7247\u9700\u8981\u4e0d\u505c\u91cd\u65b0\u84b8\u934d\uff0c\u96f6\u4ef6\u6bc0\u640d\u4e5f\u9700\u8981\u66f4\u65b0\u3002\u5341\u516d\u540b\u671b\u9060\u93e1\u8207\u516b\u540b\u671b\u9060\u93e1\u771f\u6b63\u6d3e\u5f97\u4e0a\u7528\u5834\u7684\u6642\u5019\u4e26\u4e0d\u591a\uff0c\u4f7f\u7528\u72c0\u6cc1\u6642\u597d\u6642\u58de\uff0c\u66f4\u591a\u6642\u5019\u88ab\u9592\u7f6e\u8457\u3002\u793e\u5718\u5e79\u90e8\u5011\u9010\u6f38\u627e\u5230\u501f\u7528\u570b\u5916\u9032\u53e3\u671b\u9060\u93e1\u7684\u7ba1\u9053\uff0c\u6c88\u541b\u5c71\u4e5f\u70ba\u793e\u5718\u5f35\u7f85\u5230\u66f4\u597d\u7684\u89c0\u6e2c\u5100\u5668\u3002"},{type:"image",url:"/img/history/the-big-bang-4.jpg",description:"\u5442\u52a9\u589e\u8001\u5e2b\u8207\u9673\u901a\u8001\u5e2b\u7684\u771f\u7a7a\u84b8\u934d\u6a5f"},{type:"paragraph",text:"1988 \u5e74\u6e05\u5927\u7269\u7406\u9928\u62c6\u9077\uff0c\u5929\u6587\u53f0\u5f9e\u78e8\u93e1\u6a5f\u5230\u671b\u9060\u93e1\u9019\u6240\u6709\u7684\u81ea\u88fd\u6210\u679c\u5c31\u5728\u642c\u9077\u904e\u7a0b\u4e2d\u4f5a\u5931\u3002"}]},{shortAuthorName:"\u4f5c\u8005/\u5085\u55bb\u3001\u6b50\u67cf\u6607",author:"\u5085\u55bb\u3001\u6b50\u67cf\u6607",coverUrl:"/img/history/zhen-kong-zheng-du-jing-mian-3.jpg",slug:"zhen-kong-zheng-du-jing-mian",title:"\u771f\u7a7a\u84b8\u934d\u93e1\u9762",recommended:!0,contents:[{type:"subtitle",text:"\u6e05\u5927\u5929\u6587\u793e\u7684\u5bf6\u8c9d"},{type:"paragraph",text:"\u4e94\u5341\u5e74\u524d\u6e05\u83ef\u5929\u6587\u793e\u5275\u793e\u521d\u5275\u4e4b\u6642\uff0c\u5e7e\u4f4d\u793e\u54e1\u5408\u4f5c\u5b8c\u6210\u4e00\u500b\u8017\u6642\u8cbb\u529b\u7684\u5927\u8a08\u756b\uff1a\u78e8\u88fd\u5341\u516d\u540b\u53cd\u5c04\u5f0f\u671b\u9060\u93e1\uff0c\u4e26\u5728\u5b78\u6821\u540c\u610f\u4e4b\u4e0b\u642d\u5efa\u4e86\u5929\u6587\u53f0\u3002\u7d93\u904e\u65e5\u591c\u8f2a\u73ed\u78e8\u88fd\u6578\u500b\u6708\u5f8c\uff0c\u7d42\u65bc\u6210\u529f\u88fd\u4f5c\u4e86\u5168\u53f0\u93e1\u9762\u6700\u5927\u7684\u671b\u9060\u93e1\uff0c\u8b93\u71b1\u611b\u661f\u661f\u7684\u6e05\u83ef\u5929\u6587\u793e\u5b78\u5b50\u5f97\u4ee5\u4ef0\u671b\u5f97\u66f4\u6df1\u66f4\u9060\u3002"},{type:"paragraph",text:"\u7136\u800c\uff0c\u671b\u9060\u93e1\u7684\u88fd\u4f5c\u4e26\u4e0d\u662f\u4e00\u52de\u6c38\u9038\uff01\u4e8b\u5be6\u4e0a\uff0c\u5341\u516d\u540b\u93e1\u9762\u7684\u300c\u92c1\u5c64\u300d\u975e\u5e38\u5bb9\u6613\u6c27\u5316\uff0c\u4f7f\u671b\u9060\u93e1\u55aa\u5931\u805a\u5149\u6548\u679c\u3002\u800c\u4e14\u8001\u5929\u6587\u53f0\u7684\u5713\u9802\u5bc6\u5408\u5ea6\u4e0d\u826f\uff0c\u4f7f\u5f97\u5929\u6587\u53f0\u5167\u6fd5\u5ea6\u96e3\u4ee5\u63a7\u5236\uff0c\u5100\u5668\u4fdd\u5b58\u4e0d\u6613\u3002\u6797\u5bcc\u6e90\u8a18\u5f97\uff0c\u7576\u6642\u4ed6\u5011\u6703\u62ff\u5341\u516d\u540b\u93e1\u4f86\u89c0\u6e2c\u884c\u661f\uff0c\u7136\u800c\u5f71\u50cf\u54c1\u8cea\u9010\u6f38\u60e1\u5316\u3002"},{type:"paragraph",text:"\u6240\u5e78\uff0c\u7576\u6642\u7684\u6e05\u5927\u7269\u7406\u7cfb\u6709\u4e00\u53f0\u300c\u771f\u7a7a\u84b8\u934d\u6a5f\u300d\uff0c\u53ef\u4ee5\u70ba\u93e1\u7247\u4e00\u6b21\u6b21\u91cd\u65b0\u934d\u819c\u3002"},{type:"subtitle",text:"\u771f\u7a7a\u84b8\u934d\u93e1\u7684\u904e\u7a0b"},{type:"paragraph",text:"\u6e05\u83ef\u4e4b\u6240\u4ee5\u8cfc\u5165\u9019\u53f0\u771f\u7a7a\u84b8\u934d\u6a5f\uff0c\u5176\u5be6\u662f\u70ba\u4e86\u534a\u5c0e\u9ad4\u7814\u7a76\u7528\u9014\uff0c\u4f46\u5b83\u7684\u76f4\u5f91\u70ba\u5341\u516d\u540b\uff0c\u6070\u597d\u53ef\u4ee5\u88dd\u5f97\u4e0b\u5b78\u751f\u642c\u4f86\u7684\u93e1\u7247\u3002"},{type:"paragraph",text:"\u5728\u7269\u7406\u7cfb\u9673\u901a\u6559\u6388\u548c\u5442\u52a9\u589e\u6559\u6388\u7684\u6307\u5c0e\u4e0b\uff0c\u540c\u5b78\u9996\u5148\u7528\u4e19\u7850\u6e05\u6d17\u6389\u5df2\u7d93\u6c27\u5316\u7684\u8001\u820a\u934d\u5c64\uff0c\u63a5\u8457\u5c0f\u5fc3\u7ffc\u7ffc\u628a\u93e1\u7247\u5f9e\u5929\u6587\u53f0\u642c\u5230\u5be6\u9a57\u5ba4\uff0c\u653e\u9032\u771f\u7a7a\u84b8\u934d\u6a5f\uff0c\u5c07\u771f\u7a7a\u84b8\u934d\u6a5f\u5167\u90e8\u7a7a\u6c23\u62bd\u76e1\u5f8c\uff0c\u52a0\u71b1\u8b93\u92c1\u5747\u52fb\u5730\u9644\u4e0a\u93e1\u9762\uff0c\u5b8c\u6210\u4e4b\u5f8c\u518d\u5c0f\u5fc3\u7ffc\u7ffc\u5c07\u93e1\u7247\u642c\u56de\u5929\u6587\u53f0\u3002"},{type:"subtitle",text:"\u934d\u93e1\u662f\u4ef6\u82e6\u5dee\u4e8b"},{type:"paragraph",text:"\u6700\u8b93\u793e\u53cb\u5011\u5370\u8c61\u6df1\u523b\u7684\uff0c\u5176\u5be6\u4e0d\u662f\u5982\u4f55\u64cd\u4f5c\u6a5f\u5668\u84b8\u934d\uff0c\u800c\u662f\u8981\u628a\u91cd\u9054\u5e7e\u5341\u516c\u65a4\u93e1\u7247\u642c\u4e0b\u6a13\uff0c\u6a13\u68af\u975e\u5e38\u50be\u659c\u53c8\u72f9\u7a84\u50c5\u5bb9\u4e00\u4eba\uff0c\u4e00\u5931\u624b\u53ef\u80fd\u5c31\u628a\u524d\u4eba\u7684\u5fc3\u8840\u90fd\u5f04\u58de\u4e86\u3002"},{type:"paragraph",text:"\u5982\u6b64\u8aaa\u4f86\uff0c\u934d\u93e1\u5176\u5be6\u662f\u76f8\u7576\u9ebb\u7169\u7684\u5dee\u4e8b\uff01\u56e0\u6b64\u5929\u6587\u793e\u7684\u540c\u5b78\u5011\u9592\u4f86\u7121\u4e8b\u4e0d\u6703\u53bb\u934d\u93e1\uff0c\u934d\u93e1\u7684\u6642\u6a5f\u5f80\u5f80\u662f\u5728\u8209\u8fa6\u5927\u578b\u6d3b\u52d5\u4e4b\u524d\uff0c\u4e5f\u5c31\u662f\u8aaa\uff0c\u773e\u4eba\u5354\u529b\u934d\u93e1\uff0c\u5c31\u610f\u5473\u8457\u793e\u5718\u7684\u91cd\u8981\u7684\u89c0\u6e2c\u6d3b\u52d5\u5373\u5c07\u958b\u59cb\uff01"},{type:"image",url:"/img/history/zhen-kong-zheng-du-jing-mian-3.jpg",description:"1980\u5e74\u4ee3\u6e05\u5927\u5929\u6587\u793e\u771f\u7a7a\u84b8\u934d\u93e1\u9762\u5be6\u9a57\u7167\u7247\u3002\u5de6\u4e8c\u70ba1984\u5e74\u793e\u9577\u6797\u5bcc\u6e90\u5148\u751f\uff0c\u53f3\u4e8c\u70ba\u4e2d\u7814\u9662\u5929\u6587\u6240\u91d1\u5347\u5149\u535a\u58eb\u3002\uff08\u5716\uff0f\u6797\u5bcc\u6e90\u63d0\u4f9b\uff09"},{type:"image",url:"/img/history/zhen-kong-zheng-du-jing-mian-2.jpg",description:"\u6e05\u83ef\u5927\u5b78\u771f\u7a7a\u84b8\u934d\u8a2d\u5099\u3002\uff08\u5716\uff0f\u6e05\u5927\u5929\u6587\u793e\u63d0\u4f9b\uff09"},{type:"image",url:"/img/history/zhen-kong-zheng-du-jing-mian-1.jpg",description:"\u6e05\u83ef\u5927\u5b78\u771f\u7a7a\u84b8\u934d\u8a2d\u5099\uff08\u84cb\u4e0a\u9032\u884c\u84b8\u934d\u6642\uff09\u3002\uff08\u5716\uff0f\u6e05\u5927\u5929\u6587\u793e\u63d0\u4f9b\uff09"}]},{shortAuthorName:"\u4f5c\u8005/\u5085\u55bb",author:"\u5085\u55bb",coverUrl:"/img/history/science-monthly-1.jpg",slug:"science-monthly",title:"\u5e7c\u82d7\u8207\u79d1\u5b78\u6708\u520a",recommended:!0,contents:[{type:"paragraph",text:"\u300c\u79d1\u5b78\u6708\u520a\u300d\u5275\u520a\u65bc 1970 \u5e74\uff0c\u300c\u6e05\u5927\u5929\u6587\u793e\u300d\u5275\u793e\u65bc 1971 \u5e74\u3002\u5169\u8005\u8a95\u751f\u7684\u6642\u9593\u76f8\u8fd1\u7d14\u5c6c\u5de7\u5408\u55ce\uff1f\u4ed4\u7d30\u758f\u7406\u5169\u8005\u7684\u8a95\u751f\u5951\u6a5f\uff0c\u6211\u5011\u53ef\u4ee5\u6e05\u695a\u77e5\u9053\u9019\u4e26\u4e0d\u662f\u4e00\u6a01\u5de7\u5408\uff0c\u751a\u81f3\u63a5\u8fd1\u7cfb\u51fa\u540c\u6e90\u3002"},{type:"paragraph",text:"\u79d1\u5b78\u6708\u520a\u7684\u5275\u7acb\uff0c\u4f86\u81ea\u65bc\u4e00\u7fa4\u7559\u5b78\u7f8e\u570b\u7684\u7406\u5de5\u79d1\u77e5\u8b58\u4efd\u5b50\uff0c\u6709\u611f\u65bc\u570b\u5167\u77e5\u8b58\u6c34\u5e73\u4f4e\u843d\uff0c\u5e0c\u671b\u63a8\u884c\u79d1\u666e\u555f\u8fea\u6c11\u667a\uff0c\u63d0\u5347\u6574\u9ad4\u6c11\u65cf\u5be6\u529b\u3002\u79c9\u6301\u8457\u79d1\u5b78\u6551\u570b\u7684\u7406\u60f3\u62b1\u8ca0\uff0c\u81ea\u638f\u8170\u5305\uff0c\u9694\u8457\u6d32\u8207\u6d32\u66f8\u4fe1\u5f80\u8fd4\uff0c\u9694\u8457\u7f8e\u570b\u8207\u81fa\u7063\u806f\u7e6b\u51fa\u7248\u4e8b\u52d9\uff0c\u6210\u529f\u51fa\u7248\u4e86\u54c1\u8cea\u7d55\u4f73\u7684\u672c\u571f\u79d1\u666e\u520a\u7269\uff0c\u4e94\u5341\u5e74\u591a\u5e74\u4f86\u672a\u66fe\u4e2d\u65b7\u904e\uff0c\u6642\u81f3\u4eca\u65e5\uff0c\u79d1\u5b78\u6708\u520a\u90fd\u4ecd\u7136\u662f\u6307\u6a19\u6027\u7684\u520a\u7269\u3002"},{type:"paragraph",text:"\u9019\u4e9b\u79d1\u5b78\u6708\u520a\u7684\u767c\u8d77\u4eba\uff0c\u6bcf\u4e00\u4f4d\u90fd\u5c0d\u6642\u4ee3\u7522\u751f\u4e86\u6df1\u523b\u7684\u5f71\u97ff\uff0c\u5176\u4e2d\u6709\u5169\u4f4d\u767c\u8d77\u4eba\u8207\u6e05\u5927\u5929\u6587\u793e\u6709\u8457\u5bc6\u5207\u7684\u95dc\u4fc2\u2014\u2014\u674e\u6021\u56b4\u548c\u6c88\u541b\u5c71\uff0c\u4ed6\u5011\u5206\u5225\u662f\u6e05\u5927\u5929\u6587\u793e\u7684\u7b2c\u4e8c\u4f4d\u548c\u7b2c\u4e09\u4f4d\u6307\u5c0e\u8001\u5e2b\u3002"},{type:"paragraph",text:"\u7ffb\u7ffb 1970 \u5e74\u4ee3\u5404\u671f\u7684\u79d1\u5b78\u6708\u520a\uff0c\u6211\u5011\u53ef\u4ee5\u770b\u5230\u674e\u6021\u56b4\u548c\u6c88\u541b\u5c71\u5169\u4eba\u7684\u540d\u5b57\u4e0d\u505c\u51fa\u73fe\u5728\u51fa\u7248\u9801\u8207\u6587\u7ae0\u4f5c\u8005\u6b04\u4f4d\u3002\u674e\u6021\u56b4\u66fe\u7d93\u662f\u767c\u884c\u4eba\u548c\u7763\u5370\u4eba\u7b49\uff0c\u8b80\u8005\u4fe1\u7bb1\u4e5f\u56e0\u4ed6\u800c\u5c07\u5730\u5740\u8a2d\u5728\u6e05\u83ef\u5927\u5b78\u7269\u7406\u6240\uff1b\u6c88\u541b\u5c71\u70ba\u5404\u671f\u8ca2\u737b\u4e86\u8c50\u5bcc\u7684\u7a3f\u91cf\uff0c\u65e2\u6709\u7d14\u7cb9\u7684\u79d1\u666e\u77e5\u8b58\u4ecb\u7d39\uff0c\u4e5f\u6709\u91dd\u5c0d\u8a72\u5982\u4f55\u79d1\u666e\u7684\u6642\u8ad6\u3002\u5169\u4eba\u66f4\u540c\u70ba\u79d1\u5b78\u6708\u520a\u55ae\u884c\u672c\u7269\u7406\u7de8\u8b6f\u79d1\u5b78\u8ad6\u53e2\u4e2d\u7684\u300c\u7269\u7406\u8ad6\u53e2\u300d\u7684\u4e3b\u7de8\u3002"},{type:"paragraph",text:"\u200b\u200b\u4e0d\u8ad6\u662f\u51fa\u7248\u520a\u7269\u53c8\u6216\u662f\u6276\u690d\u5b78\u751f\u6210\u7acb\u793e\u5718\uff0c\u90fd\u662f 1970 \u5e74\u4ee3\u77e5\u8b58\u4efd\u5b50\u5be6\u8e10\u7406\u60f3\u7684\u624b\u6bb5\u3002"},{type:"paragraph",text:"\u4e5f\u8a31\u6b63\u662f\u56e0\u70ba\u9019\u6a23\u7684\u4eba\u969b\u7db2\u7d61\uff0c\u6e05\u5927\u5929\u6587\u793e\u8207\u79d1\u5b78\u6708\u520a\u4e4b\u9593\u64c1\u6709\u7a69\u56fa\u7684\u6a4b\u6a11\uff0c\u4f7f\u5f97 1970 \u5e74\u4ee3\u521d\u671f\uff0c\u6e05\u5927\u5929\u6587\u793e\u54e1\u4e5f\u627f\u8972\u4e86\u6559\u6388\u5011\u7684\u79d1\u666e\u7cbe\u795e\uff0c\u5728\u793e\u5167\u7de8\u64b0\u591a\u671f\u3008\u5929\u6587\u901a\u8a0a\u3009\uff0c\u4e3b\u52d5\u641c\u96c6\u8cc7\u6599\u5beb\u6210\u6587\u7ae0\u4f9b\u793e\u54e1\u4e4b\u9593\u5f7c\u6b64\u5207\u78cb\uff0c\u751a\u81f3\u82b1\u8cbb\u5fc3\u529b\u570b\u5916\u7684\u671f\u520a\u8ad6\u6587\u7de8\u8b6f\u6210\u4e2d\u6587\u3002"},{type:"paragraph",text:""},{type:"paragraph",text:"\u6e05\u5927\u5929\u6587\u793e\u7684\u793e\u54e1\u591a\u6b21\u5728\u79d1\u5b78\u6708\u520a\u767c\u8868\u6587\u7ae0\uff1a"},{type:"paragraph",text:"\u27a9 \u53c3\u8207\u5275\u793e\u7b2c\u4e00\u5e74\u81ea\u88fd\u671b\u9060\u93e1\u8a08\u756b\u5b78\u751f\u9673\u660e\u8aa0\u3001\u5f90\u9b41\u68ee\u548c\u9ec3\u9298\u7235\u7b49\u4eba\uff0c\u7de8\u5beb\u5b8c\u6574\u7684\u6559\u5b78\u6b65\u9a5f\uff0c\u81ea 1971 \u5e74 8 \u6708\u7b2c 20 \u671f\u81f3 1972 \u5e74 9 \u6708\u7b2c 33 \u671f\uff0c\u4ee5\u7b2c\u4e00\u4f4d\u6307\u5c0e\u8001\u5e2b\u7684\u540d\u7fa9\u4e18\u5b8f\u7fa9\u5728\u79d1\u5b78\u6708\u520a\u9023\u8f09\u4e86\u5341\u56db\u7bc7\u3008\u671b\u9060\u93e1\u81ea\u88fd\u6cd5\u3009\uff0c\u4e26\u5728\u9023\u8f09\u7d50\u675f\u5f8c\u51fa\u7248\u6210\u70ba\u300a\u5982\u4f55\u81ea\u88fd\u5929\u6587\u5100\u5668\u300b\u3002"},{type:"paragraph",text:"\u27a9 \u79d1\u5b78\u6708\u520a\u7684\u7814\u7a76\u767c\u5c55\u90e8\u66fe\u5728 1974 \u5e74\u7b2c 50 \u671f\uff0c\u70ba\u6e05\u5927\u5929\u6587\u793e\u5beb\u4e86\u4e00\u7bc7\u7c21\u77ed\u7684\u6982\u8ff0\u3008\u5e7c\u82d7\u6e05\u5927\u5929\u6587\u793e\u7c21\u4ecb\u3009\uff0c\u6b64\u6642\u6e05\u5927\u5929\u6587\u793e\u7684\u78ba\u5c31\u5982\u540c\u5e7e\u4f4d\u6559\u6388\u5408\u529b\u7a2e\u4e0b\u7684\u5e7c\u82d7\u3002"},{type:"paragraph",text:"\u27a9 1974 \u5e74\u7b2c 54 \u671f\u7b2c\u56db\u5c46\u793e\u9577\u9ec3\u4e00\u8fb2\u767c\u8868\u3008\u5c0f\u884c\u661f\u3009\u300156 \u671f\u7b2c\u4e00\u5c46\u793e\u9577\u9127\u5ef6\u748b\u767c\u8868\u3008\u4e00\u4f4d\u5927\u7269\u7406\u5b78\u5bb6\u862d\u9053\u7684\u751f\u5e73\u3009\u3001\u7b2c 57 \u671f\u7b2c\u4e09\u5c46\u793e\u9577\u694a\u932b\u6b3d\u767c\u8868\u3008\u5316\u5b78\u8207\u85dd\u8853\u54c1\uff1a\u5047\u53e4\u8463\u7684\u71b1\u5149\u9451\u5b9a\u6cd5\u3009\u30011975 \u5e74\u7b2c 61 \u671f\u9ec3\u4e00\u8fb2\u767c\u8868\u3008\u571f\u661f\u2014\u2014\u6700\u7f8e\u9e97\u7684\u661f\u9ad4\u3009\u3001\u7b2c 62 \u671f\u9ec3\u4e00\u8fb2\u767c\u8868\u3008\u5929\u738b\u661f\u3009\u3001\u7b2c 66 \u671f\u9127\u5ef6\u748b\u767c\u8868\u3008\u96fb\u78c1\u98db\u8eca\u3009\u3001\u694a\u932b\u6b3d\u767c\u8868\u3008\u661f\u969b\u4efd\u5b50\u77e5\u591a\u5c11\u3009\u7b49\u3002"},{type:"image",url:"/img/history/science-monthly-4.jpg",description:"\u5c01\u9762"},{type:"image",url:"/img/history/science-monthly-2.jpg",description:"\u5167\u6587\u624b\u7a3f"},{type:"image",url:"/img/history/science-monthly-3.jpg",description:"\u5c01\u5e95"},{type:"paragraph",text:"\u534a\u4e16\u7d00\u5f8c\u7684\u4eca\u5929\uff0c\u6e05\u5927\u5929\u6587\u793e\u7684\u793e\u8fa6\u751a\u81f3\u6709\u81ea\u5df1\u7684\u4f3a\u670d\u5668\u3001\u7121\u7dda\u7db2\u8def\u3001\u96fb\u8166\u3001\u96d9\u87a2\u5e55\u3001\u5f71\u5370\u6a5f\u548c\u6383\u63cf\u6a5f\uff0c\u4efb\u4f55\u793e\u54e1\u90fd\u53ef\u4ee5\u81ea\u7531\u4f7f\u7528\u3002\u5b78\u6821\u70ba\u6211\u5011\u4ed8\u8cbb\u8cfc\u8cb7\u7684\u6578\u4f4d\u8cc7\u6599\u5eab\uff0c\u53c8\u6216\u662f\u570b\u5167\u5916\u514d\u8cbb\u8cc7\u6e90\uff0c\u6211\u5011\u8207\u77e5\u8b58\u7684\u8ddd\u96e2\uff0c\u7d55\u5c0d\u6bd4\u534a\u500b\u4e16\u7d00\u4ee5\u524d\u66f4\u8fd1\u3002\u6b63\u662f\u56e0\u70ba\u9019\u500b\u8ddd\u96e2\u6536\u6582\uff0c\u4f7f\u6211\u5011\u7ffb\u95b1\u8001\u820a\u7684\u79d1\u5b78\u6708\u520a\u8207\u793e\u520a\u6642\uff0c\u66f4\u9707\u61fe\u65bc\u524d\u4eba\u70ba\u4e86\u514b\u670d\u8ddd\u96e2\u505a\u51fa\u7684\u52aa\u529b\u3002"}]},{shortAuthorName:"\u4f5c\u8005/\u5085\u55bb",author:"\u5085\u55bb",coverUrl:"/img/history/the-earliest-astronomy-camp-1.jpg",slug:"the-earliest-astronomy-camp",title:"\u6700\u65e9\u7684\u5929\u6587\u71df\uff0c\u5713\u5c71\u6e05\u5927\u5169\u982d\u8dd1\uff01",recommended:!1,contents:[{type:"paragraph",text:"\u6e05\u5927\u5929\u6587\u793e\u66fe\u7d93\u5728\u6c11\u570b 66 \u5b78\u5e74\u5ea6\uff081977 \u5e74\uff09\u6691\u5047\u81f3\u6c11\u570b 71 \u5b78\u5e74\u5ea6\uff081982 \u5e74\uff09\u6691\u5047\u8209\u884c\u5929\u6587\u71df\uff0c\u6709\u898f\u5283\u5730\u5411\u5168\u570b\u5404\u5927\u5c08\u9662\u6821\u62db\u6536\u71df\u968a\u5b78\u54e1\u3002"},{type:"paragraph",text:"\u6b64\u6642\u7684\u5929\u6587\u71df\u662f\u914d\u5408\u6551\u570b\u5718\u300c\u6691\u671f\u81ea\u5f37\u6d3b\u52d5\u5927\u5c08\u5b78\u751f\u5b78\u751f\u7814\u7a76\u968a\u7c4c\u7d44\u8fa6\u6cd5\u300d\u8209\u884c\uff0c\u6551\u570b\u5718\u7684\u8fa6\u6cd5\u4e2d\u660e\u78ba\u5beb\u51fa\u7c4c\u7d44\u7814\u7a76\u5c0d\u7684\u76ee\u7684\u5728\u65bc\u300c\u4f7f\u7406\u8ad6\u8207\u5be6\u969b\u76f8\u7d50\u5408\uff0c\u4ee5\u62d3\u5c55\u5b78\u8853\u7814\u7a76\u9818\u57df\u300d\uff0c\u800c\u5929\u6587\u71df\u7d55\u5c0d\u662f\u9019\u500b\u7406\u5ff5\u7684\u6700\u4f73\u9ad4\u73fe\uff01"},{type:"paragraph",text:"\u71df\u968a\u70ba\u671f\u4e94\u5929\uff0c\u53ef\u4ee5\u5206\u6210\u4e09\u500b\u4e3b\u8981\u90e8\u5206\uff1a"},{type:"paragraph",text:"\u2776 \u767d\u5929\u5e7e\u4e4e\u5168\u662f\u5b78\u8005\u6f14\u8b1b\uff0c\u5929\u6587\u793e\u9080\u96c6\u4e86\u5168\u53f0\u91cd\u8981\u7684\u5929\u6587\u5b78\u8005\uff0c\u5e6b\u5b78\u54e1\u5e36\u4f86\u8db3\u5177\u6df1\u5ea6\u7684\u5167\u5bb9\u3002\u66fe\u7d93\u53c3\u8207\u8b1b\u5b78\u7684\u5b78\u8005\u8207\u8ab2\u7a0b\u5305\u542b\u4e86\uff1a\u200b\u200b\u6c88\u541b\u5c71 / \u6f2b\u8ac7\u5929\u6587\u3001\u8521\u7ae0\u737b / \u5929\u6587\u53f0\u64cd\u4f5c\u3001\u962e\u570b\u5168 / \u661f\u8c61\u89c0\u6e2c\u3001\u76e7\u4e16\u658c / \u5929\u6587\u89c0\u6e2c\u5100\u5668\u3001\u9673\u5fd7\u8070 / \u5929\u6587\u651d\u5f71\u3001\u5b6b\u65b9\u9438 / \u8ecc\u9053\u8a08\u7b97\u3001\u9673\u901a / \u771f\u7a7a\u934d\u93e1\u3001\u8cf4\u5fe0\u4fe1 / \u5149\u5b78\u5be6\u9a57\u3001\u502a\u7dad\u6597 / \u5929\u6587\u7269\u7406\u3001\u674e\u4fca\u5f18 / \u65e9\u671f\u5929\u6587\u5b78\u8207\u5929\u6587\u767c\u5c55\u3002"},{type:"paragraph",text:"\u2777 \u591c\u665a\u5b89\u6392\u89c0\u6e2c\u6d3b\u52d5\uff0c\u6b64\u6642\u5929\u6587\u793e\u81ea\u88fd\u7684\u5341\u516d\u540b\u671b\u9060\u93e1\u548c\u516b\u540b\u671b\u9060\u93e1\u5c31\u6210\u4e86\u7126\u9ede\uff01\u7136\u800c\u5b78\u751f\u81ea\u88fd\u7684\u671b\u9060\u93e1\u54c1\u8cea\u7d42\u7a76\u6709\u7591\u616e\uff0c\u56e0\u6b64\u5929\u6587\u793e\u53c8\u5411\u65b0\u7af9\u4e2d\u5b78\u3001\u65b0\u7af9\u793e\u6559\u9928\u3001\u660e\u9053\u4e2d\u5b78\u501f\u7528\u591a\u67b6\u671b\u9060\u93e1\uff0c\u5176\u4e2d\uff0c\u65b0\u7af9\u793e\u6559\u9928\u5728 1976 \u5e74\u5f9e\u65e5\u672c\u9032\u53e3\u4e86\u7cbe\u826f\u7684\u6298\u5c04\u5f0f\u671b\u9060\u93e1\uff0c\u7136\u800c\u793e\u6559\u9928\u4e26\u7121\u4eba\u61c2\u5f97\u4f7f\u7528\uff0c\u6e05\u5927\u5929\u6587\u793e\u5f9e\u6b64\u6210\u4e86\u793e\u6559\u9928\u671b\u9060\u93e1\u7684\u4e3b\u8981\u4f7f\u7528\u8005\u3002\u85c9\u7531\u71df\u968a\u6d3b\u52d5\uff0c\u5c0d\u5916\u5efa\u7acb\u8d77\u7dca\u5bc6\u7684\u5408\u4f5c\u95dc\u4fc2\uff0c\u793e\u5718\u7684\u904b\u4f5c\u4e0d\u518d\u4fb7\u9650\u65bc\u6821\u5167\uff0c\u800c\u80fd\u5ef6\u4f38\u89f8\u89d2\u3002"},{type:"paragraph",text:"\u2778 \u5176\u4e2d\u4e00\u65e5\u524d\u5f80\u53f0\u5317\u5713\u5c71\u5929\u6587\u53f0\u53c3\u89c0\uff0c\u5713\u5c71\u5929\u6587\u53f0\u7684\u53f0\u9577\u8521\u7ae0\u737b\u548c\u6280\u5de5\u962e\u570b\u5168\u4e00\u76f4\u4ee5\u4f86\u5c0d\u6e05\u5927\u5929\u6587\u793e\u5475\u8b77\u5099\u81f3\uff0c\u6e05\u5927\u5929\u6587\u793e\u8209\u8fa6\u5168\u570b\u6027\u6d3b\u52d5\u6642\uff0c\u4ed6\u5011\u7e3d\u662f\u4e0d\u754f\u8fe2\u8fe2\u8def\u7a0b\u4f86\u5230\u65b0\u7af9\u8b1b\u5b78\uff0c\u4e26\u5c07\u6e05\u5927\u5929\u6587\u793e\u7684\u6d3b\u52d5\u8a0a\u606f\u516c\u544a\u5230\u300a\u5929\u6587\u5feb\u5831\u300b\uff0c\u8b93\u5168\u570b\u76f8\u95dc\u793e\u7fa4\u77e5\u9053\u3002"},{type:"image",url:"/img/history/the-earliest-astronomy-camp-1.jpg",description:"\u6e05\u83ef\u5927\u5b78\u6c88\u541b\u5c71\u9662\u9577\u5728\u8ac7\u5929\u6587\u65b0\u77e5\uff0c\u64da\u5b78\u751f\u56de\u61b6\uff0c\u6c88\u541b\u5c71\u6559\u6388\u7279\u5225\u559c\u6b61\u8b1b\u9ed1\u6d1e\u4e3b\u984c"},{type:"image",url:"/img/history/the-earliest-astronomy-camp-2.jpg",description:"\u4e2d\u592e\u5927\u5b78\u738b\u986f\u9054\u8001\u5e2b\u8b1b\u6388\u592a\u967d\u7cfb\u7684\u5404\u7a2e\u60c5\u5f62"},{type:"image",url:"/img/history/the-earliest-astronomy-camp-3.jpg",description:"\u660e\u9053\u4e2d\u5b78\u5929\u6587\u53f0\u53f0\u9577\u76e7\u4e16\u658c\u8001\u5e2b\u8b1b\u8ff0\u671b\u9060\u93e1\u7684\u69cb\u9020"},{type:"image",url:"/img/history/the-earliest-astronomy-camp-4.jpg",description:"\u5713\u5c71\u5929\u6587\u53f0\u53f0\u9577\u8521\u7ae0\u737b\u5728\u8b1b\u8ff0\u89c0\u661f\u6280\u5de7"},{type:"image",url:"/img/history/the-earliest-astronomy-camp-5.jpg",description:"\u5713\u5c71\u5929\u6587\u53f0\u6280\u58eb\u962e\u570b\u5168\u5728\u8b1b\u8ff0\u91ce\u5916\u89c0\u6e2c\u7684\u6280\u5de7"},{type:"image",url:"/img/history/the-earliest-astronomy-camp-6.jpg",description:"\u6e05\u83ef\u5927\u5b78\u9673\u5fd7\u8070\u8001\u5e2b\u5728\u8b1b\u8ff0\u5929\u6587\u651d\u5f71"}]},{shortAuthorName:"\u4f5c\u8005/\u5085\u55bb",author:"\u5085\u55bb",coverUrl:"/img/history/halleys-comet-4.jpg",slug:"halleys-comet",title:"\u54c8\u96f7\u5f57\u661f\u6f38\u9732\u82b3\u8e64\uff0c\u5929\u6587\u793e\u4e00\u76f4\u638c\u63e1\u4ed6\u7684\u884c\u5f91",recommended:!0,contents:[{type:"paragraph",text:"\u611f\u8b1d\u96fb\u6a5f\u7cfb 84 \u7d1a\u5f35\u666f\u65b0\u5b78\u9577\u7559\u7d66\u793e\u5718\u7684\u7c21\u5831\u672c\u3002"},{type:"paragraph",text:"1986 \u5e74\u54c8\u96f7\u5f57\u661f\u56de\u6b78\uff0c\u8208\u8d77\u4e00\u80a1\u8ffd\u661f\u71b1\uff0c\u6e05\u5927\u5929\u6587\u793e\u66f4\u70ba\u6b64\u5c55\u958b\u4e86\u4e00\u9023\u4e32\u7684\u6d3b\u52d5\uff0c\u6253\u5f9e\u5f57\u661f\u56de\u6b78\u4e4b\u524d\u7684\u6578\u500b\u6708\uff0c\u4fbf\u8209\u8fa6\u4e86\u7814\u8a0e\u6703\u3001\u7de8\u8f2f\u54c8\u96f7\u5f57\u661f\u5c08\u865f\uff0c\u4e26\u9080\u8acb\u5b78\u8005\u6f14\u8b1b\uff0c\u597d\u8b93\u793e\u54e1\u5011\u66f4\u52a0\u8a8d\u8b58\u9019\u4f4d\u8cb4\u5ba2\u7684\u8eab\u4e16\uff1b\u5f57\u661f\u56de\u6b78\u671f\u9593\uff0c\u66f4\u662f\u7dca\u947c\u5bc6\u9f13\u5730\u8209\u884c\u5927\u5927\u5c0f\u5c0f\u7684\u89c0\u6e2c\u3002"},{type:"paragraph",text:"\u89c0\u6e2c\u5f57\u661f\u5f9e\u4f86\u5c31\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\uff0c\u5728\u5f57\u661f\u5373\u5c07\u903c\u8fd1\u4e4b\u6642\uff0c\u5168\u81fa\u7684\u8ffd\u661f\u597d\u624b\u722d\u5148\u6050\u5f8c\u60f3\u7576\u6700\u5148\u89c0\u6e2c\u5230\u5f57\u661f\u6216\u662f\u6700\u5148\u62cd\u4e0b\u54c8\u96f7\u5f57\u661f\u7684\u4eba\u3002\u6e05\u83ef\u5929\u6587\u793e\u4e5f\u53c3\u8207\u4e86\u9019\u9805\u8ffd\u661f\u7af6\u722d\uff0c\u4ee5\u5e73\u5730\u7684\u89c0\u6e2c\u800c\u8a00\uff0c\u6e05\u83ef\u6355\u6349\u5230\u5f57\u661f\u8e64\u5f71\u7684\u78ba\u662f\u5168\u53f0\u6578\u4e00\u6578\u4e8c\u3002"},{type:"paragraph",text:"\u60f3\u77e5\u9053\u66f4\u591a\u793e\u5718\u8207\u54c8\u96f7\u5f57\u661f\u7684\u7de3\u5206\uff0c\u6b61\u8fce\u53c3\u8003\u672c\u7db2\u9801\u300c\u793e\u53cb\u4f86\u7a3f\u300d\u5340\u584a\u4e2d\u7b2c 13 \u5c46\u793e\u9577\u6797\u5bcc\u6e90\u5b78\u9577\u7684\u6587\u7ae0\u3008\u7576\u5e74\u6211\u5011\u4e00\u8d77\u8ffd\u54c8\u96f7\u7684\u65e5\u5b50\u3009"},{type:"paragraph",text:"\u672c\u6587\u5716\u7247\u70ba\u793e\u5718\u7684\u7b2c 11 \u5c46\u5e79\u90e8\u5f35\u666f\u65b0\u5b78\u9577\uff08\u96fb\u6a5f 84 \u7d1a\uff09\u907a\u7559\u7684\u7c21\u5831\uff0c\u5f9e\u5168\u570b\u6027\u548c\u5730\u653e\u6027\u5831\u5c0e\u53ef\u77e5\uff0c\u6e05\u5927\u5929\u6587\u793e\u6258\u5f57\u661f\u7684\u798f\u53d7\u5230\u77da\u76ee\u3002\u5f57\u661f\u56de\u6b78\u4e4b\u4e0b\u7684\u5929\u6587\u53f0\uff0c\u4e0d\u50c5\u5c6c\u65bc\u6e05\u83ef\u5b78\u5b50\uff0c\u66f4\u6210\u70ba\u793e\u5340\u5927\u773e\u9ad4\u6703\u5929\u6587\u610f\u8da3\u7684\u7d55\u4f73\u7ba1\u9053\u3002"},{type:"image",url:"/img/history/halleys-comet-1.jpg",description:""},{type:"image",url:"/img/history/halleys-comet-2.jpg",description:""},{type:"image",url:"/img/history/halleys-comet-3.jpg",description:""},{type:"image",url:"/img/history/halleys-comet-4.jpg",description:""},{type:"image",url:"/img/history/halleys-comet-5.jpg",description:""},{type:"image",url:"/img/history/halleys-comet-6.jpg",description:""},{type:"image",url:"/img/history/halleys-comet-7.jpg",description:""}]}]}}]);